class Setting {
  Setting({
    String? id,
    String? data,
    String? description,
  }) {
    _id = id;
    _data = data;
    _description = description;
  }

  Setting.fromJson(dynamic json) {
    _id = json['id'];
    _data = json['data'];
    _description = json['description'];
  }

  String? _id;
  String? _data;
  String? _description;

  Setting copyWith({
    String? id,
    String? data,
    String? description,
  }) =>
      Setting(
        id: id ?? _id,
        data: data ?? _data,
        description: description ?? _description,
      );

  String? get id => _id;

  String? get data => _data;

  String? get description => _description;

  Map<String, dynamic> toJson() {
    final map = <String, dynamic>{};
    map['id'] = _id;
    map['data'] = _data;
    map['description'] = _description;
    return map;
  }
}
